#!/usr/bin/env node

var sys = require('sys'),
    maker = require('node-build'),
    when  = maker.when;

maker.define("sub").
	perform.
	echo("In sub")

maker.define("first").perform.
        echo("In first").
        call("sub")

maker.define("second").dependsOn("first").
        perform.
        echo("In second")

maker.achieve("second");
