tasks.setVersion=function(options) {
        sys.log("setting version number to " +options.version + " in file " + options.file);
        var data = fs.readFileSync(options.file, 'utf8');
        data = data.replace("${VERSION}", options.version);
        fs.writeFileSync(options.file, data);
        done();
    };
tasks.targetProduction=function(options) {
        sys.log("targeting environment to production for file " + options.file);
        var data = fs.readFileSync(options.file, 'utf8');
        data = data.replace("development", "production");
        fs.writeFileSync(options.file, data);
        done();
    };

language_definition.setVersion={arguments: [{type: "direct", name: "file"},{type: "direct", name: "version"}]};
language_definition.targetProduction={arguments: [{type: "direct", name: "file"}]};


    