 tasks.scp=function(options) {
    sys.log("Copying "+options.source+ " to "+options.target+" on "+options.host);
    tasks.exec({cmd :"scp '"+options.source+"' '"+options.host+":"+options.target+"'"});
};
tasks.ssh=function(options) {
    sys.log("Performing "+options.cmd+ " on "+options.host);
    tasks.exec({cmd: "echo \"("+options.cmd+"); exit $?\" | ssh "+options.host});
};

language_definition.scp = {arguments: [{type: "direct", name: "host"},{type: "direct", name: "source"},{type: "direct", name: "target"}]};
language_definition.ssh = {arguments: [{type: "direct", name: "host"},{type: "direct", name: "cmd"}]};
   